INSERT INTO `newpaper` (`id`, `title`, `link`, `thumb`, `excrept`, `id_cate`) VALUES
(1, 'Chủ tịch nước Nguyễn Xuân Phúc ân giảm án tử hình cho 4 phạm nhân', '/xa-hoi/chu-tich-nuoc-nguyen-xuan-phuc-an-giam-an-tu-hinh-cho-4-pham-nhan-20220128205748609.htm', 'https://icdn.dantri.com.vn/zoom/210_140/2022/01/28/cnt-crop-1643378073947.jpeg', ' Thực hiện thẩm quyền của Chủ tịch nước và thể hiện chính sách nhân đạo của Nhà nước, Chủ tịch nước Nguyễn Xuân Phúc ký quyết định về việc ân giảm án tử hình xuống tù chung thân cho 4 phạm nhân. ', 492),
(2, 'Ông Nguyễn Văn Yên giữ chức Phó Trưởng Ban Nội chính Trung ương', '/xa-hoi/ong-nguyen-van-yen-giu-chuc-pho-truong-ban-noi-chinh-trung-uong-20220128163135021.htm', 'https://icdn.dantri.com.vn/zoom/210_140/2022/01/28/noichinh116433579717511798428297-crop-1643362204291.jpeg', ' Ban Bí thư Trung ương Đảng bổ nhiệm ông Nguyễn Văn Yên - Vụ trưởng Vụ Theo dõi xử lý các vụ án, vụ việc, Ban Nội chính Trung ương, giữ chức Phó Trưởng Ban Nội chính Trung ương. ', 492),
(3, 'Thủ tướng: Không &quot;ngăn sông cấm chợ&quot; cản người dân về quê ăn Tết', '/xa-hoi/thu-tuong-khong-ngan-song-cam-cho-can-nguoi-dan-ve-que-an-tet-20220128152907108.htm', 'https://icdn.dantri.com.vn/zoom/210_140/2022/01/28/tet-1-crop-1643358380944.jpeg', ' Thủ tướng Phạm Minh Chính yêu cầu các tỉnh thành đón người dân từ các nơi trở về quê trong dịp Tết Nguyên đán an toàn, không &quot;ngăn sông cấm chợ&quot;. ', 492),
(4, 'Khai trừ Đảng cựu Chủ tịch tỉnh Khánh Hòa Lê Đức Vinh', '/xa-hoi/khai-tru-dang-cuu-chu-tich-tinh-khanh-hoa-le-duc-vinh-20220128131722127.htm', 'https://icdn.dantri.com.vn/zoom/210_140/2022/01/28/screenshot20220107064243zalo-1643350220532.jpeg', ' Cựu Chủ tịch Khánh Hòa Lê Đức Vinh bị khai trừ Đảng vì đã có những vi phạm rất nghiêm trọng, để lại nhiều hệ lụy khó khắc phục, gây bức xúc trong dư luận, ảnh hưởng xấu đến uy tín của tổ chức Đảng. ', 492),
(5, 'Thủ tướng: Không để &quot;tháng Giêng là tháng ăn chơi&quot;!', '/xa-hoi/thu-tuong-khong-de-thang-gieng-la-thang-an-choi-20220128114425878.htm', 'https://icdn.dantri.com.vn/zoom/210_140/2022/01/28/thu-tuong-pham-minh-chinh-1643344885418.jpeg', ' Thủ tướng Phạm Minh Chính nhấn mạnh tinh thần không để &quot;tháng Giêng là tháng ăn chơi&quot;, có những việc phải làm ngay, làm tốt, làm hiệu quả trong Tết. ', 492),
(6, 'Thủ tướng Phạm Minh Chính bổ nhiệm một số vị trí trong Quân đội', '/xa-hoi/thu-tuong-pham-minh-chinh-bo-nhiem-mot-so-vi-tri-trong-quan-doi-20220128082820616.htm', 'https://icdn.dantri.com.vn/zoom/210_140/2022/01/28/thu-tuong-1643333081740.jpeg', ' Thiếu tướng Nguyễn Văn Gấu, Chính ủy Quân khu 9, giữ chức Phó Chủ nhiệm Tổng cục Chính trị; Thiếu tướng Hồ Văn Thái, Chủ nhiệm Chính trị Quân khu 9, giữ chức Chính ủy Quân khu 9. ', 492),
(7, 'Thủ tướng giao Bộ Công an mở rộng điều tra vụ Việt Á', '/xa-hoi/thu-tuong-giao-bo-cong-an-mo-rong-dieu-tra-vu-viet-a-20220127145247898.htm', 'https://icdn.dantri.com.vn/zoom/210_140/2022/01/27/thutuong1-crop-1643269835010.jpeg', ' Thủ tướng Phạm Minh Chính giao Bộ Công an mở rộng điều tra tích cực hơn nữa vụ Việt Á để sớm đưa ra xét xử công khai, minh bạch, đúng người, đúng tội, đúng pháp luật. ', 492),
(8, 'Hà Nội xem xét xử lý kỷ luật tổ chức đảng, đảng viên tại 9 bệnh viện lớn', '/xa-hoi/ha-noi-xem-xet-xu-ly-ky-luat-to-chuc-dang-dang-vien-tai-9-benh-vien-lon-20220127121451385.htm', 'https://icdn.dantri.com.vn/zoom/210_140/2021/04/01/benh-vien-tam-than-trung-uong-i-1617243494533.jpg', ' Ủy ban Kiểm tra Thành ủy Hà Nội vừa họp, xem xét xử lý trách nhiệm kỷ luật đối với các tổ chức đảng, đảng viên tại Đảng bộ 9 bệnh viện trên địa bàn thành phố. ', 492),
(9, 'Thủ tướng dâng hương tại Khu tưởng niệm các Anh hùng liệt sĩ Pò Hèn', '/xa-hoi/thu-tuong-dang-huong-tai-khu-tuong-niem-cac-anh-hung-liet-si-po-hen-20220127111447106.htm', 'https://icdn.dantri.com.vn/zoom/210_140/2022/01/27/thu-tuong-1643256438677.jpg', ' Ngày 26/1, Thủ tướng Phạm Minh Chính và đoàn công tác của Trung ương đã dâng hương tại Khu tưởng niệm các Anh hùng liệt sĩ Pò Hèn (thành phố Móng Cái, Quảng Ninh). ', 492),
(10, 'Thủ tướng: Omicron đã xâm nhập nên tuyệt đối không chủ quan dịp Tết!', '/xa-hoi/thu-tuong-omicron-da-xam-nhap-nen-tuyet-doi-khong-chu-quan-dip-tet-20220127093552829.htm', 'https://icdn.dantri.com.vn/zoom/210_140/2022/01/27/thu-tuong-1-crop-1643250793251.jpeg', ' Thủ tướng Phạm Minh Chính nêu rõ, biến chủng Omicron đã xâm nhập vào nước ta nên chúng ta tuyệt đối không được lơ là, chủ quan, mất cảnh giác trong dịp Tết. ', 492),
(11, 'Chủ tịch nước tặng quà Tết gia đình có công, hộ nghèo tại Hải Phòng', '/xa-hoi/chu-tich-nuoc-tang-qua-tet-gia-dinh-co-cong-ho-ngheo-tai-hai-phong-20220126164356163.htm', 'https://icdn.dantri.com.vn/zoom/210_140/2022/01/26/ctn3ab7e1acfbf4847539fae4321c060f3b4-crop-1643189922180.jpeg', ' Nhân dịp Tết Nguyên đán Nhâm Dần, Chủ tịch nước Nguyễn Xuân Phúc hôm nay (26/1) đã tới thăm, tặng quà Tết gia đình người có công, nạn nhân chất độc da cam, hộ nghèo, công nhân lao động tại Hải Phòng. ', 492),
(12, 'Thủ tướng cắt băng khánh thành hai công trình trọng điểm tại Quảng Ninh', '/xa-hoi/thu-tuong-cat-bang-khanh-thanh-hai-cong-trinh-trong-diem-tai-quang-ninh-20220126112611245.htm', 'https://icdn.dantri.com.vn/zoom/210_140/2022/01/26/ttg-1643172468181.jpeg', ' Cầu Tình yêu và Đường bao biển nối TP Hạ Long - TP Cẩm Phả, hai công trình trọng điểm của tỉnh Quảng Ninh vừa được khánh thành sáng nay (26/1). ', 492),
(13, 'Chủ tịch nước Nguyễn Xuân Phúc ân giảm án tử hình cho 4 phạm nhân', '/xa-hoi/chu-tich-nuoc-nguyen-xuan-phuc-an-giam-an-tu-hinh-cho-4-pham-nhan-20220128205748609.htm', 'https://icdn.dantri.com.vn/zoom/210_140/2022/01/28/cnt-crop-1643378073947.jpeg', ' Thực hiện thẩm quyền của Chủ tịch nước và thể hiện chính sách nhân đạo của Nhà nước, Chủ tịch nước Nguyễn Xuân Phúc ký quyết định về việc ân giảm án tử hình xuống tù chung thân cho 4 phạm nhân. ', 492),
(14, 'Ông Nguyễn Văn Yên giữ chức Phó Trưởng Ban Nội chính Trung ương', '/xa-hoi/ong-nguyen-van-yen-giu-chuc-pho-truong-ban-noi-chinh-trung-uong-20220128163135021.htm', 'https://icdn.dantri.com.vn/zoom/210_140/2022/01/28/noichinh116433579717511798428297-crop-1643362204291.jpeg', ' Ban Bí thư Trung ương Đảng bổ nhiệm ông Nguyễn Văn Yên - Vụ trưởng Vụ Theo dõi xử lý các vụ án, vụ việc, Ban Nội chính Trung ương, giữ chức Phó Trưởng Ban Nội chính Trung ương. ', 492),
